mapper.select:

    SELECT 
    t.id,
    t.file_name,
    t.type,
    t.description,
    t.path,
    t.create_by,
    t.create_dt,
    t.update_by,
    t.update_dt,
    t.version


    SELECT COUNT(1) AS TOTAL_SIZE

FROM tbl_comm_file t
WHERE 1=1

    AND t.id = #{id}


    AND t.file_name = #{file_name}


    AND t.type = #{type}


    AND t.description = #{description}


    AND t.path = #{path}


    AND t.create_by = #{create_by}


    AND t.create_dt = #{create_dt,jdbcType=TIMESTAMP}


    AND t.update_by = #{update_by}


    AND t.update_dt = #{update_dt,jdbcType=TIMESTAMP}


    AND t.version = #{version}


    
        ORDER BY ${sortBy}  ${sortType}
    


    limit ${startIdx}, ${pageSize}
